import React from "react";

const Card = ({ pokemon }) => {
  console.log(pokemon);
  return (
    <div className="card">
      <div className="img">
        <img src={pokemon.sprites.front_default} />
      </div>
      <h3 className="cardName">{pokemon.name}</h3>
      <div className="cardTypes">
        <div>タイプ</div>
				{pokemon.map((_type) => {
					return _type.types;
				})}
      </div>
    </div>
  );
};

export default Card;
