const Card = ({ pokemon }) => {
  return (
    <div className="card">
      <div className="img">
        <img src={pokemon.sprites.front_default} />
      </div>
      <h3 className="cardName">{pokemon.name}</h3>
      <div className="cardTypes">
        <div>タイプ</div>
					{pokemon.types.map((_type) => {
						console.log(_type.name);
						return _type.name;
					})}
      </div>
    </div>
  );
};

export default Card;
